const axios = require('axios');
const FormData = require('form-data');

class QuqiClient {
  constructor(config) {
    this.headers = {
      'Cookie': config.cookies,
      'Referer': 'https://quqi.com/',
      'Content-Type': 'application/x-www-form-urlencoded'
    };
    this.config = config;
    this.axios = axios.create({
      headers: this.headers,
      timeout: 10000
    });
  }

  _sleep(ms) {
    return new Promise(resolve => setTimeout(resolve, ms));
  }

  async getDailyTask() {
    try {
      const response = await this.axios.get('https://quqi.com/api/task/daily');
      return response.data?.list || null;
    } catch (error) {
      console.error('获取任务失败:', error.message);
      return null;
    }
  }

  async getReward(id) {
    try {
      await this.axios.post('https://quqi.com/api/task/reward', 
        `taskId=${id}`);
    } catch (error) {
      console.error(`领取奖励失败(${id}):`, error.message);
    }
  }

  async visit(id) {
    try {
      const response = await this.axios.post(
        `https://quqi.com/api/group/visit/${id}`,
        'type=visit'
      );
      if (response.data?.code === 200) {
        console.log(`访问群组 ${id} 成功`);
      }
    } catch (error) {
      console.error(`访问群组失败(${id}):`, error.message);
    }
  }

  async getQuqiId() {
    try {
      const response = await this.axios.get('https://quqi.com/api/group/list');
      const groups = response.data?.list || [];

      for (const group of groups) {
        if (group.status === 1) return group;
      }

      // 创建新群组
      const createRes = await this.axios.post(
        'https://quqi.com/api/group/create',
        'name=AutoGroup'
      );
      return createRes.data || null;
    } catch (error) {
      console.error('获取群组失败:', error.message);
      return null;
    }
  }

  async uploadImg(id) {
    try {
      const formData = new FormData();
      // 模拟图片文件
      formData.append('file', Buffer.from('fake-image-data'), {
        filename: 'test.jpg',
        contentType: 'image/jpeg'
      });

      const uploadRes = await this.axios.post(
        `https://quqi.com/api/task/upload/${id}`,
        formData,
        { headers: formData.getHeaders() }
      );

      if (uploadRes.data?.code === 200) {
        console.log(`图片上传任务 ${id} 完成`);
        // 删除临时文件
        await this.axios.post(
          `https://quqi.com/api/task/delete/${id}`,
          `fileId=${uploadRes.data.fileId}`
        );
      }
    } catch (error) {
      console.error(`图片任务失败(${id}):`, error.message);
    }
  }

  async executeTasks() {
    try {
      const tasks = await this.getDailyTask();
      if (!tasks || tasks.length === 0) {
        return '没有需要执行的任务';
      }

      for (const task of tasks) {
        await this._sleep(1500);

        switch(task.type) {
          case 'visit':
            await this.visit(task.id);
            break;
          case 'upload':
            await this.uploadImg(task.id);
            break;
          case 'reward':
            await this.getReward(task.id);
            break;
        }
      }

      const walletRes = await this.axios.get('https://quqi.com/api/user/wallet');
      return `任务完成，当前余额：${walletRes.data.balance}`;
    } catch (error) {
      return `任务执行失败: ${error.message}`;
    }
  }
}

module.exports = QuqiClient;

