function initializeWebSocket(handleFunctionCall, url, cb) {
    const ws = new WebSocket(url);
    cb(ws);

    ws.onopen = () => {
        postMessage({ status: 'connected' });
    };

    ws.onmessage = (event) => {
        handleFunctionCall(event.data);
    };

    ws.onclose = () => {
        postMessage({ status: 'disconnected' });
        setTimeout(() => {
            initializeWebSocket(handleFunctionCall, url, cb);
        }, 1000);
    };

    ws.onerror = (error) => {
        console.error('WebSocket error:', error);
        ws.close();
