        this.websocketByteBuffer.onmessage = this.onMessageByteBuffer.bind(this);
        this.websocketByteBuffer.onerror = this.onError.bind(this);

        this.websocketInputStream.onopen = this.onOpenInputStream.bind(this);
        this.websocketInputStream.onmessage = this.onMessageInputStream.bind(this);
        this.websocketInputStream.onerror = this.onError.bind(this);
    }

    onOpenByteArray(evt) {
        this.echoBinaryByteArray();
    }

    onOpenByteBuffer(evt) {
        this.echoBinaryByteBuffer();
    }

    onOpenInputStream(evt) {
        this.echoBinaryInputStream();
    }

    echoBinaryByteArray() {
        const data = new Uint8Array([72, 101, 108, 108, 111]);
        this.websocketByteArray.send(data);
    }

    echoBinaryByteBuffer() {
        const buffer = new ArrayBuffer(5);
        const data = new Uint8Array(buffer);
        data.set([87, 111, 114, 108, 100]);
        this.websocketByteBuffer.send(buffer);
    }

    echoBinaryInputStream() {
        const blob = new Blob([new Uint8Array([33, 33, 33])]);
        this.websocketInputStream.send(blob);
    }

    onMessageByteArray(evt) {
        this.handleReceivedData(evt, 'ByteArray');
    }

    onMessageByteBuffer(evt) {
        this.handleReceivedData(evt, 'ByteBuffer');
    }

    onMessageInputStream(evt) {
        this.handleReceivedData(evt, 'InputStream');
    }

    handleReceivedData(evt, type) {
        if (evt.data instanceof ArrayBuffer) {
            const decoder = new TextDecoder();
            const text = decoder.decode(new Uint8Array(evt.data));
            this.writeToScreen(`Received ${type}: ${text}`);
        } else if (evt.data instanceof Blob) {
            const reader = new FileReader();
            reader.onload = () => {
                this.writeToScreen(`Received ${type}: ${reader.result}`);
            };
            reader.readAsText(evt.data);
        } else {
            this.writeToScreen(`Received ${type} with unknown data type`);
        }
    }

    onError(evt) {
        this.writeToScreen('WebSocket error occurred.');
    }

    writeToScreen(message) {
        const p = document.createElement('p');
        p.textContent = message;
        this.output.appendChild(p);
    }
}

