  constructor(url, protocols, options) {
    const defaults = {
      debug: false,
      automaticOpen: true,
      reconnectInterval: 1000,
      maxReconnectInterval: 30000,
      reconnectDecay: 1.5,
      timeoutInterval: 2000
    };

    options = options || {};
    for (let key in defaults) {
      this[key] = typeof options[key] !== 'undefined' ? options[key] : defaults[key];
    }

    this.url = url;
    this.reconnectAttempts = 0;
    this.readyState = WebSocket.CONNECTING;
    this.protocol = null;

    let forcedClose = false;
    let timedOut = false;
    let eventTarget = document.createElement('div');

    this.addEventListener = eventTarget.addEventListener.bind(eventTarget);
    this.removeEventListener = eventTarget.removeEventListener.bind(eventTarget);
    this.dispatchEvent = eventTarget.dispatchEvent.bind(eventTarget);

    this.open = function(reconnecting) {
      let ws = new WebSocket(this.url, protocols || []);
      if (!reconnecting) {
        this.dispatchEvent(new Event('connecting'));
      }

      let timeout = setTimeout(() => {
        timedOut = true;
        ws.close();
        timedOut = false;
      }, this.timeoutInterval);

      ws.onopen = () => {
        clearTimeout(timeout);
        this.protocol = ws.protocol;
        this.readyState = WebSocket.OPEN;
        this.reconnectAttempts = 0;
        let event = new Event('open');
        event.isReconnect = reconnecting;
        this.dispatchEvent(event);
      };

      ws.onclose = (event) => {
        clearTimeout(timeout);
        ws = null;
        if (forcedClose) {
          this.readyState = WebSocket.CLOSED;
          this.dispatchEvent(new Event('close'));
        } else {
          this.readyState = WebSocket.CONNECTING;
          let connectingEvent = new Event('connecting');
          Object.assign(connectingEvent, {
            code: event.code,
            reason: event.reason,
            wasClean: event.wasClean
          });
          this.dispatchEvent(connectingEvent);

          if (!reconnecting && !timedOut) {
            this.dispatchEvent(new Event('close'));
          }

          let timeout = this.reconnectInterval * Math.pow(this.reconnectDecay, this.reconnectAttempts);
          setTimeout(() => {
            this.reconnectAttempts++;
            this.open(true);
          }, Math.min(timeout, this.maxReconnectInterval));
        }
      };

      ws.onmessage = (event) => {
        let messageEvent = new Event('message');
        messageEvent.data = event.data;
        this.dispatchEvent(messageEvent);
      };

      ws.onerror = (event) => {
        this.dispatchEvent(new Event('error'));
      };
    };

    if (this.automaticOpen) {
      this.open(false);
    }

    this.send = function(data) {
      if (ws) {
        return ws.send(data);
      }
      throw new Error('INVALID_STATE_ERR : Pausing to reconnect websocket');
    };

    this.close = function(code, reason) {
      forcedClose = true;
      if (ws) {
        ws.close(code || 1000, reason);
      }
    };

    this.refresh = function() {
      if (ws) {
        ws.close();
      }
    };
  }

  onopen() {}
  onclose() {}
  onconnecting() {}
  onmessage() {}
  onerror() {}
}

