class LiveQueryClient {
  constructor({ applicationId, serverURL, javascriptKey, masterKey, sessionToken, installationId }) {
    this.emitter = new EventEmitter();
    this.on = this.emitter.on.bind(this.emitter);
    this.emit = this.emitter.emit.bind(this.emitter);
    if (!serverURL.startsWith('ws://') && !serverURL.startsWith('wss://')) {
      throw new Error('serverURL must start with ws:// or wss://');
    }
    this.attempts = 0;
    this.id = null;
    this.requestId = 1;
    this.applicationId = applicationId;
    this.serverURL = serverURL;
    this.javascriptKey = javascriptKey;
    this.masterKey = masterKey;
    this.sessionToken = sessionToken;
    this.installationId = installationId;
    this.additionalProperties = true;
    this.connectPromise = new Promise((resolve, reject) => {
      this._resolveConnect = resolve;
      this._rejectConnect = reject;
    });
    this.subscriptions = new Map();
    this.socket = null;
    this.state = 'INITIALIZED';
    this.reconnectHandle = null;
  }
  open() {
    if (this.state === 'CONNECTING' || this.state === 'CONNECTED') {
      return;
    }
    this.state = 'CONNECTING';
    this.socket = new WebSocketController(this.serverURL);
    this.socket.onopen = () => {
      this._handleWebSocketOpen();
    };
    this.socket.onmessage = (event) => {
      this._handleWebSocketMessage(event);
    };
    this.socket.onclose = () => {
      this._handleWebSocketClose();
    };
    this.socket.onerror = (error) => {
      this._handleWebSocketError(error);
    };
  }
  _handleWebSocketOpen() {
    const connectMessage = {
      action: 'connect',
      applicationId: this.applicationId,
      requestId: this.requestId++
    };
    if (this.javascriptKey) {
      connectMessage.javascriptKey = this.javascriptKey;
    }
    if (this.masterKey) {
      connectMessage.masterKey = this.masterKey;
    }
    if (this.sessionToken) {
      connectMessage.sessionToken = this.sessionToken;
    }
    if (this.installationId) {
      connectMessage.installationId = this.installationId;
    }
    if (this.additionalProperties) {
      connectMessage.additionalProperties = true;
    }
    this.socket.send(JSON.stringify(connectMessage));
  }
  _handleWebSocketMessage(event) {
    let message;
    try {
      message = JSON.parse(event.data);
    } catch (e) {
      this.emit('error', new Error('Failed to parse message'));
      return;
    }
    if (message.action === 'connected') {
      this.id = message.clientId;
      this.state = 'CONNECTED';
      this._resolveConnect();
      this.emit('open');
      this.attempts = 0;
      return;
    }
    if (message.subscriptionId && this.subscriptions.has(message.subscriptionId)) {
      const subscription = this.subscriptions.get(message.subscriptionId);
      subscription._handleMessage(message);
    }
  }
  _handleWebSocketClose() {
    this.state = 'INITIALIZED';
    this.emit('close');
    this._handleReconnect();
  }
  _handleWebSocketError(error) {
    this.emit('error', error);
    this.socket.close();
    this._handleReconnect();
  }
  _handleReconnect() {
    clearTimeout(this.reconnectHandle);
    const delay = Math.min(1000 * Math.pow(2, this.attempts++), 30000);
    this.reconnectHandle = setTimeout(() => {
      this.open();
    }, delay);
  }
}

