class WebSocketStore {
  constructor(snack, currentUser) {
    this.snack = snack;
    this.currentUser = currentUser;
    this.wsActive = false;
    this.ws = null;
    this.messageCallback = null;
  }

  listen(callback) {
    this.messageCallback = callback;
    if (this.wsActive && this.ws) {
      this.ws.close(1000);
      this.wsActive = false;
    }
    const token = this.currentUser.getToken();
    const wsUrl = `wss://example.com/socket?token=${token}`;
    this.ws = new WebSocket(wsUrl);
    this.wsActive = true;
    this.ws.onopen = () => {
      this.snack.report('WebSocket connected');
    };
    this.ws.onmessage = (event) => {
      try {
        const data = JSON.parse(event.data);
        if (this.messageCallback) {
          this.messageCallback(data);
        }
      } catch (error) {
        console.error('Error parsing message:', error);
        this.snack.report('Error parsing WebSocket message');
      }
    };
    this.ws.onerror = (error) => {
      console.error('WebSocket error:', error);
      this.snack.report('WebSocket error occurred');
      this.ws?.close();
    };
    this.ws.onclose = (event) => {
      this.wsActive = false;
      if (event.reason) {
        console.log(`WebSocket closed: ${event.reason}`);
      }
      if (event.code !== 1000) {
        setTimeout(() => {
          if (this.messageCallback) {
            this.listen(this.messageCallback);
          }
        }, 5000);
      }
    };
  }

  close() {
    if (this.wsActive && this.ws) {
      this.ws.close(1000);
      this.wsActive = false;
    }
  }
}

