      const isVisible = !document.hidden;
      if (isVisible !== this.windowVisible) {
        this.windowVisible = isVisible;
        this.reportFocus(isVisible);
      }
    };
    this.focusHandler = () => {
      if (!this.windowVisible) {
        this.windowVisible = true;
        this.reportFocus(true);
      }
