    return new Promise((resolve, reject) => {
      if (this.webSocket) {
        this.webSocket.close();
        this.webSocket = undefined;
        this.wsPort = undefined;
      }
      this.webSocket = new WebSocket(`ws://localhost:${addressPort}`);
      this.wsPort = addressPort;

      this.webSocket.onopen = () => resolve();
      this.webSocket.onerror = (err) => reject(err);
      this.webSocket.onmessage = this.handleMessage.bind(this);
      this.webSocket.onclose = () => {
        this.webSocket = undefined;
        this.wsPort = undefined;
      };
    });
  }

  handleMessage(event) {
    try {
      const data = JSON.parse(event.data);
      if (data.type === 'message') {
        this.dispatchEvent(new CustomEvent('messageReceived', { detail: data.payload }));
      } else if (data.type === 'settings') {
        this.dispatchEvent(new CustomEvent('settingsChanged', { detail: data.data }));
      }
    } catch (error) {
      console.error('Error parsing message:', error);
    }
  }

  disconnect() {
    if (this.webSocket) {
      this.webSocket.close();
      this.webSocket = undefined;
      this.wsPort = undefined;
    }
