function startRecording(data) {
    const socket = new WebSocket(`ws://${data.host}:${data.port}`);
    let isServerReady = false;
    let isCapturing = false;
    let mediaRecorder = null;

    socket.onopen = () => {
        socket.send(JSON.stringify({
            task: data.task,
            language: data.language,
            modelSize: data.modelSize,
            useVad: data.useVad
        }));
    };

    socket.onmessage = (event) => {
        const response = JSON.parse(event.data);
        if (response.status === 'ready') {
            isServerReady = true;
            if (mediaRecorder && mediaRecorder.state === 'inactive') {
                mediaRecorder.start(250);
                isCapturing = true;
            }
        }
        if (response.transcription) {
            browser.runtime.sendMessage({ transcription: response.transcription });
        }
        if (response.error) {
            browser.runtime.sendMessage({ error: `Server error: ${response.error}` });
        }
    };

    socket.onerror = (error) => {
        browser.runtime.sendMessage({ error: `WebSocket error: ${error.message}` });
        socket.close();
    };

    socket.onclose = () => {
        if (mediaRecorder && mediaRecorder.state !== 'inactive') {
            mediaRecorder.stop();
        }
        isCapturing = false;
        browser.runtime.sendMessage({ status: 'disconnected' });
    };

    navigator.mediaDevices.getUserMedia({ audio: true })
        .then(stream => {
            mediaRecorder = new MediaRecorder(stream, { mimeType: 'audio/webm; codecs=opus' });
            mediaRecorder.ondataavailable = (event) => {
                if (event.data.size > 0 && socket.readyState === WebSocket.OPEN) {
                    socket.send(event.data);
                }
            };
            mediaRecorder.onstart = () => {
                isCapturing = true;
                browser.runtime.sendMessage({ status: 'recording_started' });
            };
            mediaRecorder.onstop = () => {
                isCapturing = false;
                browser.runtime.sendMessage({ status: 'recording_stopped' });
            };
            if (isServerReady && mediaRecorder.state === 'inactive') {
                mediaRecorder.start(250);
            }
        })
        .catch(err => {
            browser.runtime.sendMessage({ error: `Microphone access denied: ${err.message}` });
            socket.close();
        });
}

