    constructor() {
        this.webSockets = {};
        this.socketCounter = 0;
    }

    getSocketId() {
        return new Promise((resolve, reject) => {
            const socketId = `sock${this.socketCounter++}`;
            const ws = new WebSocket('wss://chat.example.com');
            this.webSockets[socketId] = ws;

            const onError = (error) => {
                delete this.webSockets[socketId];
                reject(error);
            };

            ws.onerror = onError;

            ws.onopen = () => {
                ws.send(JSON.stringify({ type: 'handshake' }));
                resolve(socketId);
            };

            ws.onclose = () => {
                delete this.webSockets[socketId];
            };
        });
    }

    ping(socketId) {
        return new Promise((resolve, reject) => {
            const ws = this.webSockets[socketId];
            if (!ws) {
                return reject(new Error('Socket not found'));
            }
            ws.send(JSON.stringify({ type: 'ping' }));
            resolve();
        });
    }

    send(socketId, msg, onMessage) {
        return new Promise((resolve, reject) => {
            const ws = this.webSockets[socketId];
            if (!ws) {
                return reject(new Error('Socket not found'));
            }

            const handleMessage = (event) => {
                let data;
                try {
                    data = JSON.parse(event.data);
                } catch (e) {
                    return;
                }
                onMessage(data);
                if (data.type === 2) {
                    ws.removeEventListener('message', handleMessage);
                    resolve(data);
                }
            };

            const handleError = (event) => {
                ws.removeEventListener('message', handleMessage);
                reject(event.error || new Error('WebSocket error'));
            };

            const handleClose = () => {
                ws.removeEventListener('message', handleMessage);
                reject(new Error('WebSocket closed'));
            };

            ws.addEventListener('message', handleMessage);
            ws.addEventListener('error', handleError);
            ws.addEventListener('close', handleClose);

            ws.send(JSON.stringify(msg));
        });
    }

    close(socketId) {
        return new Promise((resolve) => {
            const ws = this.webSockets[socketId];
            if (!ws) {
                return resolve();
            }

            delete this.webSockets[socketId];

            const cleanup = () => {
                ws.removeEventListener('error', cleanup);
                ws.removeEventListener('close', cleanup);
                resolve();
            };

            ws.addEventListener('error', cleanup);
            ws.addEventListener('close', cleanup);

            ws.close();
        });
    }
}

