class PythonWebSocket {
    constructor(editor) {
        this.running = false;
        this.restarting = false;
        this.consoleData = [];
        this.wsOpen = false;
        this.ws = null;
        this.lineno = { offset: 0, current: 0 };
        this.editor = editor;
        this.pendingExecution = null;
    }

    initPythonWebSocket() {
        this.ws = new WebSocket('ws://localhost:8765');
        this.ws.onopen = () => {
            this.wsOpen = true;
        };
        this.ws.onmessage = (event) => {
            const data = JSON.parse(event.data);
            switch (data.type) {
                case 'output':
                    this.appendConsole(data.text);
                    break;
                case 'execution_complete':
                    if (this.pendingExecution) {
                        this.pendingExecution.resolve(data.result);
                        delete this.pendingExecution;
                    }
                    this.running = false;
                    break;
                case 'execution_error':
                    if (this.pendingExecution) {
                        this.pendingExecution.reject(data.error);
                        delete this.pendingExecution;
                    }
                    this.running = false;
                    break;
                case 'debug_line':
                    this.setLineGoThrough(data.lineno);
                    break;
                case 'kernel_restarted':
                    this.restarting = false;
                    break;
            }
        };
        this.ws.onclose = () => {
            this.wsOpen = false;
        };
    }

    runPython(code) {
        return new Promise((resolve, reject) => {
            if (!this.wsOpen) {
                reject('WebSocket not open');
                return;
            }
            this.running = true;
            this.pendingExecution = { resolve, reject };
            this.ws.send(JSON.stringify({ type: 'execute', code }));
        });
    }

    restartKernel() {
        if (this.wsOpen) {
            this.restarting = true;
            this.ws.send(JSON.stringify({ type: 'restart' }));
        }
    }

    stopDebugging() {
        if (this.wsOpen) {
            this.ws.send(JSON.stringify({ type: 'interrupt' }));
        }
    }

    appendConsole(text) {
        this.consoleData.push(text);
        const consoleElement = document.getElementById('console');
        if (consoleElement) {
            consoleElement.scrollTop = consoleElement.scrollHeight;
        }
    }

    resetConsole() {
        this.consoleData = [];
    }

    resetEditor() {
        if (this.editor && this.editor.clearBreakpoints) {
            this.editor.clearBreakpoints();
        }
    }

    setLineGoThrough(lineno) {
        if (this.editor && this.editor.setExecutionLine) {
            this.editor.setExecutionLine(lineno);
        }
    }
}

