    this.socket.onerror = this.onClose.bind(this);
    this.socket.onclose = this.onClose.bind(this);
  }

  resume() {}

  onOpen() {
    this.progress = 1;
  }

  onClose() {
    if (this.shouldAttemptReconnect) {
      clearTimeout(this.reconnectTimeoutId);
      this.reconnectTimeoutId = setTimeout(() => {
        this.start();
      }, 1000 * this.reconnectInterval);
    }
  }

  onMessage(event) {
    const wasFirstMessage = !this.established;
    this.established = true;

    if (wasFirstMessage && this.hookOnEstablished) {
      this.hookOnEstablished();
    }

    if (wasFirstMessage && this.onEstablishedCallback) {
      this.onEstablishedCallback(this);
    }

    if (this.destination) {
      this.destination.write(event.data);
    }
  }
}

