import Mitt from 'mitt';
import { Fly } from 'fly';

class CocogoatWebControl {
  private port: number;
  private token: string = '';
  private hwnd: number = 0;
  private version: string = '';
  private ev: Mitt;
  private client: Fly;
  private ws: WebSocket | undefined;
  private pendingRequests: Map<string, { resolve: (value: any) => void, reject: (reason?: any) => void }> = new Map();

  constructor(_port?: number) {
    this.port = _port ?? 32333;
    this.ev = new Mitt();
    this.client = new Fly();
  }

  async authorize(): Promise<boolean> {
    const authResponse = await this.client.post(`http://localhost:${this.port}/authorize`);
    this.token = authResponse.token;

    this.ws = new WebSocket(`ws://localhost:${this.port}?token=${this.token}`);

    this.ws.onmessage = (event) => {
      const data = JSON.parse(event.data);
      if (data.id && this.pendingRequests.has(data.id)) {
        const { resolve, reject } = this.pendingRequests.get(data.id)!;
        this.pendingRequests.delete(data.id);
        if (data.status >= 400) {
          reject({ status: data.status, body: data.body });
        } else {
          resolve({ status: data.status, body: data.body });
        }
      } else {
        this.ev.emit('message', data);
      }
    };

    await new Promise<void>((resolve, reject) => {
      if (this.ws.readyState === WebSocket.OPEN) {
        resolve();
        return;
      }
      this.ws.onopen = resolve;
      this.ws.onerror = reject;
    });

    return true;
  }

  wsInvoke(method: string, path: string, querystring?: Record<string, any>): Promise<{status: number, body: any}> {
    return new Promise((resolve, reject) => {
      if (!this.ws || this.ws.readyState !== WebSocket.OPEN) {
        reject(new Error('WebSocket not connected'));
        return;
      }

      const id = Math.random().toString(36).substring(2, 15);
      const message = JSON.stringify({
        id,
        method,
        path,
        querystring,
        token: this.token
      });

      this.pendingRequests.set(id, { resolve, reject });
      this.ws.send(message);
    });
  }
}

