const WebSocket = require('ws');
const ipcRender = require('../ipc/render-proecss');

class WebSocketClient {
    constructor() {
        this.wsClient = null;
        this.sendQueue = [];
        this.pendingReconnect = null;
    }

    fireSendQueue() {
        while (this.sendQueue.length && this.wsClient && this.wsClient.readyState === WebSocket.OPEN) {
            const request = this.sendQueue.shift();
            this.wsClient.send(request.data);
            request.deferred.resolve();
        }
    }

    clearSendQueue() {
        for (let i = this.sendQueue.length - 1; i >= 0; i--) {
            this.sendQueue[i].deferred.reject();
            this.sendQueue.splice(i, 1);
        }
    }

    planToReconnect(rpcUrl, options) {
        if (this.pendingReconnect) {
            ipcRender.notifyRenderProcessLogWarn('[lib/websocket.planToReconnect] another reconnection is pending');
            return;
        }

        this.pendingReconnect = setTimeout(() => {
            if (this.wsClient == null) {
                ipcRender.notifyRenderProcessLogWarn('[lib/websocket.planToReconnect] websocket is null');
                this.pendingReconnect = null;
                return;
            }

            if (this.wsClient.readyState === WebSocket.CONNECTING || this.wsClient.readyState === WebSocket.OPEN) {
                ipcRender.notifyRenderProcessLogWarn('[lib/websocket.planToReconnect] websocket current state is already ' + this.wsClient.readyState);
                this.pendingReconnect = null;
                return;
            }

            this.reconnect(rpcUrl, options);
            this.pendingReconnect = null;
        }, options.reconnectInterval);

        ipcRender.notifyRenderProcessLogDebug('[lib/websocket.planToReconnect] next reconnection is pending in ' + options.reconnectInterval + "ms");
    }

    init() {
        if (this.sendQueue.length) {
            this.clearSendQueue();
        }

        if (this.wsClient) {
            this.wsClient.onopen = null;
            this.wsClient.onclose = null;
            this.wsClient.onmessage = null;
            this.wsClient.terminate();
            this.wsClient = null;
        }
    }

    connect(rpcUrl, options, onOpenCallback, onCloseCallback, onMessageCallback) {
        this.init();

        this.wsClient = new WebSocket(rpcUrl);

        this.wsClient.onopen = () => {
            onOpenCallback({
                client: this.wsClient,
                url: rpcUrl
            });
            this.fireSendQueue();
        };

        this.wsClient.onclose = () => {
            let autoReconnect = false;

            if (options.reconnectInterval > 0) {
                autoReconnect = true;
                this.planToReconnect(rpcUrl, options);
            }

            onCloseCallback({
                client: this.wsClient,
                url: rpcUrl,
                autoReconnect: autoReconnect
            });
        };

        this.wsClient.onmessage = (event) => {
            let message = null;

            if (event) {
                message = event.data;
            }

            onMessageCallback({
                client: this.wsClient,
                url: rpcUrl,
                success: true,
                message: message
            });
        };

        this.wsClient.onerror = (event) => {
            // Do Nothing
        };
    }

    reconnect(rpcUrl, options) {
        if (!this.wsClient) {
            return;
        }

        const onOpenFn = this.wsClient.onopen;
        const onCloseFn = this.wsClient.onclose;
        const onMessageFn = this.wsClient.onmessage;
        const onErrorFn = this.wsClient.onerror;

        this.init();

        this.wsClient = new WebSocket(rpcUrl);
        this.wsClient.onopen = onOpenFn;
        this.wsClient.onclose = onCloseFn;
        this.wsClient.onmessage = onMessageFn;
        this.wsClient.onerror = onErrorFn;
    }

    send(requestContext) {
        const deferred = {};
        deferred.promise = new Promise((resolve, reject) => {
            deferred.resolve = resolve;
            deferred.reject = reject;
        });

        this.sendQueue.push({
            url: requestContext.url,
            data: requestContext.data,
            deferred: deferred
        });

        if (this.wsClient && this.wsClient.readyState === WebSocket.OPEN) {
            this.fireSendQueue();
        }

        return deferred.promise;
    }

    getReadyState() {
        if (!this.wsClient) {
            return null;
        }

        return this.wsClient.readyState;
    }
}

