class SnapdropServer {
  constructor(port) {
    this._wss = new WebSocket.Server({ port });
    this._rooms = {};
    this._peerCounter = 0;
    this._wss.on('connection', this._onConnection.bind(this));
    this._wss.on('headers', this._onHeaders.bind(this));
  }

  _onConnection(peer) {
    this._peerCounter++;
    peer.id = this._peerCounter;
    const req = peer.upgradeReq;
    peer.ip = req.headers['x-forwarded-for'] || req.connection.remoteAddress;
    this._joinRoom(peer);

    peer.on('message', (data) => {
      try {
        const message = JSON.parse(data);
        this._onMessage(peer, message);
      } catch (e) {}
    });

    peer.on('close', () => {
      this._leaveRoom(peer);
    });

    this._keepAlive(peer);
  }

  _onHeaders(headers, response) {
    headers.push('Set-Cookie: session=abc123; Path=/');
  }

  _onMessage(sender, message) {
    if (!message.type || !message.data) return;

    const room = this._rooms[sender.room];
    if (!room) return;

    switch (message.type) {
      case 'signal':
        if (message.target) {
          const recipient = room.find(p => p.id === message.target);
          if (recipient) {
            this._send(recipient, { type: 'signal', data: message.data, from: sender.id });
          }
        }
        break;
      case 'broadcast':
        room.forEach(peer => {
          if (peer !== sender) {
            this._send(peer, { type: 'signal', data: message.data, from: sender.id });
          }
        });
        break;
    }
  }

  _send(peer, message) {
    if (peer.readyState === WebSocket.OPEN) {
      peer.send(JSON.stringify(message));
    }
  }

  _joinRoom(peer) {
    if (!this._rooms[peer.ip]) {
      this._rooms[peer.ip] = [];
    }
    this._rooms[peer.ip].push(peer);
    peer.room = peer.ip;
  }

  _leaveRoom(peer) {
    if (this._rooms[peer.room]) {
      this._rooms[peer.room] = this._rooms[peer.room].filter(p => p !== peer);
      if (this._rooms[peer.room].length === 0) {
        delete this._rooms[peer.room];
      }
    }
  }

  _keepAlive(peer) {
    peer.isAlive = true;
    peer.heartbeat = setInterval(() => {
      if (!peer.isAlive) {
        peer.terminate();
        clearInterval(peer.heartbeat);
        return;
      }
      peer.isAlive = false;
      peer.ping(() => {});
    }, 30000);

    peer.on('pong', () => {
      peer.isAlive = true;
    });

    peer.on('close', () => {
      clearInterval(peer.heartbeat);
    });
  }
}

