    const configMatch = homeRes.data.match(/<script id="config">window\.cfg=(\{.*?\})<\/script>/s);
    if (!configMatch) throw new Error('Config extraction failed');
    const cfg = JSON.parse(configMatch[1]);

    const authRes = await axios.post(cfg.authUrl, {
      client_id: cfg.cid,
      client_secret: cfg.csecret
    });
    const token = authRes.data.token;

    const pkgRes = await axios.get(`${cfg.api}/v1/subscribed-packages`, {
      headers: { Authorization: `Bearer ${token}` }
    });
    const packages = pkgRes.data.list;

    const channels = [];
    const seen = new Set();
    for (const pkg of packages) {
      for (const ch of pkg.channels) {
        if (!seen.has(ch.id)) {
          seen.add(ch.id);
          channels.push({
            lang,
            site_id: ch.id,
            name: ch.translations?.[lang] || ch.name
          });
        }
      }
    }

    return channels;
  } catch (err) {
    throw new Error(`Channel fetch failed: ${err.message}`);
  }
}

