const WebSocket = require('ws');

class XVIZServer {
  constructor(handlers, options = {}, callback) {
    if (!handlers || handlers.length === 0) {
      throw new Error('Handlers must be provided');
    }
    this.handlers = handlers;
    const defaultOptions = {
      port: 8080,
      logger: console,
    };
    this.options = Object.assign({}, defaultOptions, options);
    this._server = new WebSocket.Server(this.options, () => {
      if (callback) {
        callback();
      }
    });
    this._server.on('connection', this.handleSession.bind(this));
    this._server.on('error', (err) => {
      this.log('WebSocket server error:', err);
    });
  }

  get server() {
    return this._server;
  }

  close(cb) {
    this._server.close(cb);
  }

  async handleSession(socket, request) {
    for (const handler of this.handlers) {
      try {
        const accepted = await handler.accept(request);
        if (accepted) {
          handler.handle(socket, request);
          return;
        }
      } catch (err) {
        this.log('Error in handler accept:', err);
      }
    }
    socket.close();
  }

  log(...msg) {
    if (this.options.logger && typeof this.options.logger.log === 'function') {
      this.options.logger.log(...msg);
    }
  }
}

module.exports = XVIZServer;

