class AphlictClientServer {
  constructor(server) {
    this._server = server;
    this._lists = {};
    this._adminServers = [];
    this._server.on('request', this._onrequest.bind(this));
  }

  _onrequest(request, response) {
    response.statusCode = 501;
    response.end('501 Not Implemented');
  }

  _parseInstanceFromPath(path) {
    const parts = path.split('/');
    return parts[1] ? decodeURIComponent(parts[1]) : 'default';
  }

  listen() {
    const wss = new WebSocket.Server({ server: this._server });
    wss.on('connection', (socket) => {
      const instance = this._parseInstanceFromPath(socket.upgradeReq.url);
      socket.instance = instance;
      this.log(`Client connected to instance ${instance}`);
      this.getListenerList(instance);

      socket.on('message', (message) => {
        try {
          const data = JSON.parse(message);
          const type = data.type;
          const instance = socket.instance;

          switch(type) {
            case 'subscribe':
              const list = this.getListenerList(instance);
              if (!list.includes(socket)) {
                list.push(socket);
              }
              break;
            case 'unsubscribe':
              if (this._lists[instance]) {
                this._lists[instance] = this._lists[instance].filter(s => s !== socket);
              }
              break;
            case 'ping':
              socket.send(JSON.stringify({ type: 'pong' }));
              break;
            case 'replay':
              const history = this.getHistory(data.min_age || 0);
              history.forEach(msg => {
                socket.send(JSON.stringify(msg));
              });
              break;
            default:
              this.trace(`Unknown message type: ${type}`);
          }
        } catch (e) {
          this.trace(`Error parsing message: ${e.message}`);
        }
      });

      socket.on('close', () => {
        const instance = socket.instance;
