const path = require('path');

function downloadIcons(url, style, callback) {
    (async () => {
        const processedIcons = [];
        const seenNames = new Set();
        const targetDir = path.join(__dirname, 'icons');
        const breakOnError = false;

        try {
            await fs.mkdir(targetDir, { recursive: true });

            const { data: iconList } = await axios.get(url, {
                headers: { Accept: 'application/json' },
                withCredentials: true
            });

            for (const icon of iconList) {
                try {
                    const name = icon.name || '';
                    const svgUrl = icon.svgUrl || '';
                    if (!name || !svgUrl) throw new Error('Invalid icon data');

                    if (seenNames.has(name)) {
                        if (breakOnError) throw new Error(`Duplicate name: ${name}`);
                        console.warn(`Skipping duplicate: ${name}`);
                        continue;
                    }

                    const filePath = path.join(targetDir, `${name}.svg`);
                    try {
                        await fs.access(filePath);
                        if (breakOnError) throw new Error(`File exists: ${name}`);
                        console.warn(`File exists, skipping: ${name}`);
                        continue;
                    } catch {}

                    seenNames.add(name);

                    const { data: svgContent } = await axios.get(svgUrl, { responseType: 'text' });
                    await fs.writeFile(filePath, svgContent);

                    processedIcons.push({
                        name,
                        svg: svgContent,
                        category: icon.category || '',
                        style,
                        tags: icon.tags || [],
                        code: icon.code || null,
                        unicode: icon.unicode || '',
                        pro: icon.pro || false
                    });
                } catch (err) {
                    if (breakOnError) throw err;
                    console.error(`Process icon error: ${icon.name}`, err);
                }
            }

            processedIcons.sort((a, b) => a.name.localeCompare(b.name));
            callback(null, processedIcons);
        } catch (err) {
            callback(err);
        }
    })();
}

