class EventSource {
  static get CONNECTING() { return 0; }
  static get OPEN() { return 1; }
  static get CLOSED() { return 2; }

  constructor(url) {
    this.URL = url;
    this.readyState = EventSource.CONNECTING;
    this._pollTimer = null;
    this._xhr = null;
    this._buffer = '';
    this._partialLine = '';
    this._currentData = '';
    this._currentType = '';
    this._currentLastEventId = '';
    this._handlers = {};
    this.poll();
  }

  poll() {
    if (this.readyState === EventSource.CLOSED) return;
    this._xhr = new XMLHttpRequest();
    const xhr = this._xhr;
    xhr.open('GET', this.URL);
    xhr.setRequestHeader('Accept', 'text/event-stream');
    xhr.onreadystatechange = () => {
      if (xhr.readyState === XMLHttpRequest.HEADERS_RECEIVED) {
        this.readyState = EventSource.OPEN;
      }
    };
    xhr.onprogress = () => {
      const text = xhr.responseText;
      this._processChunk(text);
    };
    xhr.onerror = () => {
      this._handleError();
    };
    xhr.onload = () => {
      if (this.readyState !== EventSource.CLOSED) {
        this._handleError();
      }
    };
    xhr.send();
    this._scheduleReconnect();
  }

  _processChunk(chunk) {
    this._buffer += chunk;
    const lines = this._buffer.split('\n');
    const incomplete = this._buffer[this._buffer.length - 1] !== '\n';
    if (incomplete) {
      this._partialLine = lines.pop();
    } else {
      this._partialLine = '';
    }
    for (let line of lines) {
      this._processLine(line);
    }
    this._buffer = this._partialLine || '';
  }

  _processLine(line) {
    line = line.trim();
    if (line.startsWith('event:')) {
      this._currentType = line.substring(6).trim();
    } else if (line.startsWith('data:')) {
      this._currentData += line.substring(5) + '\n';
