const request = require('request');

class DataManURL {
  constructor(url, type, options) {
    this.url = url;
    this._type = type || null;
    this.urlOpts = options || {};
    this._size = null;
  }

  getBuffer(callback) {
    request.get(this.url, { ...this.urlOpts, encoding: null }, (err, res, body) => {
      if (err) return callback(err);
      if (res.statusCode < 200 || res.statusCode >= 300) {
        return callback(new Error(`HTTP error ${res.statusCode}`));
      }
      this._type = res.headers['content-type'] || this._type;
      const contentLength = parseInt(res.headers['content-length'], 10);
      if (!isNaN(contentLength)) {
        this._size = contentLength;
      }
      callback(null, body);
    });
  }

  getDataUri(callback) {
    this.getBuffer((err, buffer) => {
      if (err) return callback(err);
      const type = this._type ? this._type : '';
      callback(null, `data:${type};base64,${buffer.toString('base64')}`);
    });
  }

  createReadStream() {
    return request.get(this.url, this.urlOpts);
  }

  size(callback) {
    if (this._size !== null) {
      return callback(null, this._size);
    }
    request.head(this.url, this.urlOpts, (err, res) => {
      if (err) return callback(err);
      if (res.statusCode < 200 || res.statusCode >= 300) {
        return callback(new Error(`HTTP head error ${res.statusCode}`));
      }
      const contentLength = parseInt(res.headers['content-length'], 10);
      if (!isNaN(contentLength)) {
        this._size = contentLength;
        callback(null, this._size);
      } else {
        callback(new Error('Content-Length header missing'));
      }
    });
  }

  type() {
    return this._type;
  }
}

