const express = require('express');
const path = require('path');

class FOAAS {
  constructor(options) {
    this.app = express();
    this.operations = {};
    this.operationsArray = [];

    this.app.use(express.json());
    this.app.use(express.urlencoded({ extended: true }));

    this.app.get('/', this.sendIndex.bind(this));

    this.operations = {
      off: {
        path: '/off/:name',
        method: 'GET',
        handler: (params) => ({
          message: 'F*** off',
          subtitle: `from ${params.name}`
        })
      },
      you: {
        path: '/you/:name',
        method: 'GET',
        handler: (params) => ({
          message: 'F*** you',
          subtitle: `from ${params.name}`
        })
      },
      hello: {
        path: '/hello/:name',
        method: 'GET',
        handler: (params) => ({
          message: 'Hello there',
          subtitle: `from ${params.name}`
        })
      }
    };

    this.operationsArray = Object.values(this.operations);

    Object.values(this.operations).forEach((op) => {
      const method = op.method.toLowerCase();
      this.app[method](op.path, (req, res) => {
        const result = op.handler(req.params);
        this.output(req, res, result.message, result.subtitle);
      });
    });
  }

  sendIndex(req, res) {
    res.sendFile(path.join(__dirname, 'index.html'));
  }

  output(req, res, message, subtitle) {
    res.json({
      message: message,
      subtitle: subtitle,
      headers: req.headers,
      method: req.method,
      path: req.path
    });
  }

  start(port) {
    if (this.operationsArray.length === 0) {
      throw new Error('No operations available');
    }
    this.app.listen(port, () => {
      console.log(`FOAAS server listening on port ${port}`);
    });
