const express = require('express');
const rest = require('connect-rest');
const bodyParser = require('body-parser');
const http = require('http');
const domain = require('domain');
const cluster = require('cluster');
const Attraction = require('./models/attraction');

function startServer() {
  if (cluster.isMaster) {
    const numWorkers = require('os').cpus().length;
    for (let i = 0; i < numWorkers; i++) {
      cluster.fork();
    }
    cluster.on('exit', (worker, code, signal) => {
      cluster.fork();
    });
    return;
  }

  const app = express();
  const server = http.createServer(app);

  app.use(bodyParser.json());

  const restOptions = {
    context: '/api',
    onError: (req, res, err) => {
      console.error('REST error:', err);
      res.status(500).json({ error: 'Internal Server Error' });
    }
  };
  const restMiddleware = rest.create(restOptions);
  app.use(restMiddleware);

  const services = {
    getAttractions: (req, content, cb) => {
      Attraction.find({ approved: true }, (err, attractions) => {
        if (err) return cb({ error: 'Database error', code: 500 });
        cb(null, attractions);
      });
    },
    createAttraction: (req, content, cb) => {
      const requiredFields = ['name', 'description', 'location', 'email'];
      for (const field of requiredFields) {
        if (!content[field]) {
          return cb({ error: `Missing required field: ${field}`, code: 400 });
        }
      }
      const newAttraction = new Attraction({
        name: content.name,
        description: content.description,
        location: content.location,
        email: content.email
      });
      newAttraction.save((err, saved) => {
        if (err) return cb({ error: 'Database error', code: 500 });
        cb(null, { id: saved._id });
      });
    },
    getAttractionById: (req, content, cb) => {
      Attraction.findById(req.params.id, (err, attraction) => {
        if (err) return cb({ error: 'Database error', code: 500 });
        if (!attraction) return cb({ error: 'Not found', code: 404 });
        cb(null, attraction);
      });
    }
  };

  app.rest.addServices(services);

  const serverDomain = domain.create();
  serverDomain.on('error', (err) => {
    console.error('Domain error:', err.stack);
    server.close(() => {
      process.exit(1);
    });
  });

  serverDomain.run(() => {
    const port = process.env.PORT || 3000;
    server.listen(port, () => {
      console.log(`Worker ${process.pid} listening on port ${port}`);
    });

    server.on('error', (err) => {
      serverDomain.emit('error', err);
    });
  });

  return server;
}

module.exports = startServer;

