const Prediction = require('./src/models/Prediction');
const PageView = require('./src/models/PageView');
const { shouldPrefetch, getPreviousPageId } = require('./helpers');

function setupExpressServer() {
  const app = express();
  app.use(cors());
  app.use(bodyParser.json());

  mongoose.connect(config.dbUri, { useNewUrlParser: true, useUnifiedTopology: true })
    .catch(err => console.error('MongoDB connection error:', err));

  app.post('/track-view', async (req, res) => {
    try {
      const { pageId } = req.body;
      const previousPageId = getPreviousPageId(req);

      const pageView = new PageView({ pageId, previousPageId });
      await pageView.save();

      let prefetchPath = null;
      if (shouldPrefetch(previousPageId, pageId)) {
        prefetchPath = `/page/${previousPageId}-${pageId}`;
      }

      await Prediction.findOneAndUpdate(
        { fromPage: previousPageId, toPage: pageId },
        { $inc: { count: 1 } },
        { upsert: true, new: true }
      );

      res.status(200).json({ message: 'View tracked', prefetchPath });
    } catch (error) {
      res.status(500).json({ error: 'Internal server error' });
    }
  });

  app.get('/predictions', async (req, res) => {
    try {
      const predictions = await Prediction.find().sort({ count: -1 }).limit(10);
      res.status(200).json(predictions);
    } catch (error) {
      res.status(500).json({ error: 'Failed to fetch predictions' });
    }
  });

