function setupServer() {
  const express = require('express');
  const app = express();

  // Middleware to manipulate HTTP headers
  app.use((req, res, next) => {
    res.setHeader('X-Content-Type-Options', 'nosniff');
    res.removeHeader('X-Powered-By');
    next();
  });

  // Serve static files from 'public' and 'assets' directories
  app.use(express.static('public'));
  app.use(express.static('assets'));

  // Dynamic JavaScript route
  app.get('/dynamic.js', (req, res) => {
    res.type('application/javascript');
    res.send(`console.log("Dynamic content served at: ${new Date().toISOString()}");`);
  });

  // Start the server
  app.listen(8080, 'localhost', () => {
    console.log('Server running on http://localhost:8080');
  });
}

