const express = require('express');
const hbs = require('express-handlebars');

function getServer(P) {
  const app = express();
  const port = P ?? process.env.PORT ?? 8000;
  const isReference = port === 8000;
  process.title = isReference ? 'regression-server-ref' : 'regression-server-dev';

  app.engine('handlebars', hbs.engine());
  app.set('view engine', 'handlebars');
  app.set('views', isReference ? `${process.cwd()}/reference/views` : `${process.cwd()}/dev/views`);
  app.use(express.static('public'));

  app.get('/test/:component', (req, res) => {
    res.render('component', {
      config: {
        component: req.params.component,
        mode: isReference ? 'reference' : 'development',
        port: port
      }
    });
  });

  app.listen(port);
  return app;
}
