function setupExpressServer() {
    const app = express();

    app.use(express.json());
    app.use(express.urlencoded({ extended: true }));
    app.use(connect.cookieParser());

    app.get('/email/auth', (req, res) => {
        if (!auth.isAuthenticated()) {
            return res.status(401).send('Unauthorized');
        }
        res.send({ authenticated: true });
    });

    app.post('/email/send-test', async (req, res) => {
        const { to, subject, text } = req.body;
        try {
            const transporter = nodemailer.createTransport({
                host: processInfo.emailHost,
                port: processInfo.emailPort,
                secure: processInfo.emailSecure,
                auth: {
                    user: processInfo.emailUser,
                    pass: lcrypto.decrypt(processInfo.emailPass)
                }
            });

            await transporter.sendMail({
                from: processInfo.emailFrom,
                to,
                subject,
                text
            });

            res.send({ success: true });
        } catch (error) {
            res.status(500).send({ success: false, error: error.message });
        }
    });

    app.post('/email/config/save', (req, res) => {
        const config = req.body;
        try {
            lutil.writeConfigFile('email-server-config.json', config);
            res.send({ saved: true });
        } catch (err) {
            res.status(500).send({ saved: false, error: err.message });
        }
    });

    app.listen(processInfo.port, () => {
        console.log(`Email service running on port ${processInfo.port}`);
    });
