    const app = express();
    const PORT = 3000;
    const IMAGE_PATH = path.join(__dirname, 'CapitalOne_Digital_Skills_Infographic_BW.jpg');

    app.use(responseTime());

    app.get('/non-streaming', (req, res) => {
        fs.readFile(IMAGE_PATH, (err, data) => {
            if (err) {
                res.status(500).send('Error reading file');
                return;
            }
            res.header('Content-Type', 'image/jpeg');
            res.send(data);
        });
    });

    app.get('/streaming', (req, res) => {
        res.header('Content-Type', 'image/jpeg');
        fs.createReadStream(IMAGE_PATH).pipe(res);
    });

    app.get('/non-streaming-sync', (req, res) => {
        try {
            const data = fs.readFileSync(IMAGE_PATH);
            res.header('Content-Type', 'image/jpeg');
            res.send(data);
        } catch (err) {
            res.status(500).send('Error reading file synchronously');
        }
    });

    app.get('/streaming-pipe', (req, res) => {
        res.header('Content-Type', 'image/jpeg');
        fs.createReadStream(IMAGE_PATH).pipe(res);
    });

    app.listen(PORT, () => {
        console.log(`Server running on http://localhost:${PORT}`);
    });
