const express = require('express');
const http = require('http');
const socketIO = require('socket.io');
const path = require('path');

function startServer() {
    const app = express();
    const server = http.createServer(app);
    const io = socketIO(server);

    app.use(express.static(path.join(__dirname, 'public')));

    const particles = {};
    const clusters = {};

    function generateClusterId() {
        return Math.random().toString(36).substring(2, 15) + Math.random().toString(36).substring(2, 15);
    }

    function createParticle(socketId) {
        const radius = 10;
        const x = Math.random() * (window.innerWidth - 2 * radius) + radius;
        const y = Math.random() * (window.innerHeight - 2 * radius) + radius;
        const velocity = { x: (Math.random() - 0.5) * 2, y: (Math.random() - 0.5) * 2 };
        const clusterId = generateClusterId();

        particles[socketId] = {
            id: socketId,
            x,
            y,
            radius,
            velocity,
            clusterId
        };

        if (!clusters[clusterId]) {
            clusters[clusterId] = { particles: {}, mass: 0, x: 0, y: 0, velocity: { x: 0, y: 0 } };
        }

        clusters[clusterId].particles[socketId] = particles[socketId];
        updateClusterStats(clusterId);
    }

    function updateClusterStats(clusterId) {
        const cluster = clusters[clusterId];
        const particleList = Object.values(cluster.particles);
        const totalMass = particleList.reduce((sum, p) => sum + (p.radius / 2), 0);
        const centerX = particleList.reduce((sum, p) => sum + p.x * (p.radius / 2), 0) / totalMass;
        const centerY = particleList.reduce((sum, p) => sum + p.y * (p.radius / 2), 0) / totalMass;

        cluster.mass = totalMass;
        cluster.x = centerX;
        cluster.y = centerY;
    }

    function checkCollisions(particleA, particleB) {
        const dx = particleB.x - particleA.x;
        const dy = particleB.y - particleA.y;
        const distance = Math.sqrt(dx * dx + dy * dy);
        return distance < particleA.radius + particleB.radius;
    }

    function handleCollision(particleA, particleB) {
        if (particleA.clusterId === particleB.clusterId) return;

        const tempVelocityA = { ...particleA.velocity };
        const tempVelocityB = { ...particleB.velocity };

        particleA.velocity.x = ((particleA.radius - particleB.radius) * tempVelocityA.x + 2 * particleB.radius * tempVelocityB.x) / (particleA.radius + particleB.radius);
        particleA.velocity.y = ((particleA.radius - particleB.radius) * tempVelocityA.y + 2 * particleB.radius * tempVelocityB.y) / (particleA.radius + particleB.radius);
        particleB.velocity.x = ((particleB.radius - particleA.radius) * tempVelocityB.x + 2 * particleA.radius * tempVelocityA.x) / (particleB.radius + particleA.radius);
        particleB.velocity.y = ((particleB.radius - particleA.radius) * tempVelocityB.y + 2 * particleA.radius * tempVelocityA.y) / (particleB.radius + particleA.radius);

        const angle = Math.atan2(particleB.y - particleA.y, particleB.x - particleA.x);
        const overlap = particleA.radius + particleB.radius - Math.sqrt((particleB.x - particleA.x) ** 2 + (particleB.y - particleA.y) ** 2);
        const offsetX = overlap * Math.cos(angle) / 2;
        const offsetY = overlap * Math.sin(angle) / 2;

        particleA.x -= offsetX;
        particleA.y -= offsetY;
        particleB.x += offsetX;
        particleB.y += offsetY;

        mergeClusters(particleA.clusterId, particleB.clusterId);
    }

    function mergeClusters(clusterIdA, clusterIdB) {
        if (clusterIdA === clusterIdB) return;

        const clusterA = clusters[clusterIdA];
        const clusterB = clusters[clusterIdB];

        for (const pid in clusterB.particles) {
            clusterA.particles[pid] = clusterB.particles[pid];
            clusterA.particles[pid].clusterId = clusterIdA;
        }

        updateClusterStats(clusterIdA);
        delete clusters[clusterIdB];
    }

    function updateParticles() {
        for (const id in particles) {
            const particle = particles[id];
            particle.x += particle.velocity.x;
            particle.y += particle.velocity.y;

            if (particle.x - particle.radius < 0 || particle.x + particle.radius > window.innerWidth) {
                particle.velocity.x *= -1;
                particle.x = Math.max(particle.radius, Math.min(window.innerWidth - particle.radius, particle.x));
            }

            if (particle.y - particle.radius < 0 || particle.y + particle.radius > window.innerHeight) {
                particle.velocity.y *= -1;
                particle.y = Math.max(particle.radius, Math.min(window.innerHeight - particle.radius, particle.y));
            }
        }

        const particleIds = Object.keys(particles);
        for (let i = 0; i < particleIds.length; i++) {
            for (let j = i + 1; j < particleIds.length; j++) {
                const p1 = particles[particleIds[i]];
                const p2 = particles[particleIds[j]];
                if (checkCollisions(p1, p2)) {
                    handleCollision(p1, p2);
                }
            }
        }
    }

    setInterval(() => {
        updateParticles();
        io.emit('update', { particles, clusters });
    }, 1000 / 60);

    io.on('connection', (socket) => {
        console.log('New client connected');
        createParticle(socket.id);

        socket.on('disconnect', () => {
            console.log('Client disconnected');
            const leavingParticle = particles[socket.id];
            if (leavingParticle) {
                const cluster = clusters[leavingParticle.clusterId];
                if (cluster && cluster.particles[socket.id]) {
                    delete cluster.particles[socket.id];
                    if (Object.keys(cluster.particles).length === 0) {
                        delete clusters[leavingParticle.clusterId];
                    } else {
                        updateClusterStats(leavingParticle.clusterId);
                    }
                }
                delete particles[socket.id];
            }
        });

        socket.on('input', (data) => {
            const particle = particles[socket.id];
            if (particle) {
                if (data.left) particle.velocity.x = -2;
                if (data.right) particle.velocity.x = 2;
                if (data.up) particle.velocity.y = -2;
                if (data.down) particle.velocity.y = 2;
            }
        });
    });

    server.listen(3000, () => {
        console.log('Server running on port 3000');
    });
}

