async function setupExpressServer() {
    const app = express();
    const port = process.env.PORT || 3000;

    // Middleware
    app.use(cors());
    app.use(express.json());
    app.use(rateLimit({
        windowMs: 15 * 60 * 1000,
        max: 100
    }));

    // Static files
    app.use(express.static(path.join(__dirname, 'public')));

    // Route to query elements
    app.post('/query', async (req, res) => {
        const { url, selectors } = req.body;
        if (!url || !selectors || !Array.isArray(selectors)) {
            return res.status(400).json({ error: 'Invalid request parameters' });
        }

        try {
            const browser = await puppeteer.launch();
            const page = await browser.newPage();
            await page.goto(url, { waitUntil: 'networkidle2' });

            const results = [];
            for (const selector of selectors) {
                const element = await page.$(selector);
                if (element) {
                    const text = await page.evaluate(el => el.innerText, element);
                    const href = await page.evaluate(el => el.href, element);
                    results.push({ selector, text, href });
                } else {
                    results.push({ selector, error: 'Element not found' });
                }
            }

            await browser.close();
            res.json({ results });
        } catch (error) {
            res.status(500).json({ error: error.message });
        }
    });

    // Route to get full HTML
    app.post('/fullHtml', async (req, res) => {
        const { url } = req.body;
        if (!url) {
            return res.status(400).json({ error: 'URL is required' });
        }

        try {
            const browser = await puppeteer.launch();
            const page = await browser.newPage();
            await page.goto(url, { waitUntil: 'networkidle2' });
            const html = await page.content();
            await browser.close();
            res.json({ html });
        } catch (error) {
            res.status(500).json({ error: error.message });
        }
    });

    // Serve index.html
    app.get('/', (req, res) => {
        res.sendFile(path.join(__dirname, 'public', 'index.html'));
    });

    // Start server
    app.listen(port, () => {
        console.log(`Server running on port ${port}`);
    });
}

module.exports = setupExpressServer;

