const http = require('http');
const fs = require('fs');
const path = require('path');

class Server {
  constructor(options) {
    this.options = Object.assign({
      livereloadPath: '/livereload.js',
      port: 35729
    }, options);
    this.clients = {};
    this.server = http.createServer(this.handler.bind(this));
    this.app = null;
    this.port = this.options.port;
  }

  configure(app) {
    this.app = app;
    return this;
  }

  handler(req, res, next) {
    if (this.app) {
      this.app(req, res, next || (() => {}));
    } else if (req.url === this.options.livereloadPath) {
      this.livereload(req, res);
    } else {
      if (next) next();
      else res.end();
    }
  }

  livereload(req, res) {
    const filePath = path.join(__dirname, 'public', 'livereload.js');
    fs.stat(filePath, (err, stats) => {
      if (err) {
        res.writeHead(404);
        res.end('Not Found');
        return;
      }
      res.writeHead(200, {
        'Content-Type': 'application/javascript',
        'Content-Length': stats.size
      });
      fs.createReadStream(filePath).pipe(res);
    });
  }

  listen(port, fn) {
    this.port = port || this.port;
    this.server.listen(this.port, () => {
      if (fn) fn();
    });

    this.server.on('error', this.error.bind(this));
    this.server.on('upgrade', (req, socket, head) => {
      const clientId = req.headers['sec-websocket-key'];
      if (clientId) {
        this.clients[clientId] = { req, socket, head };
      }
    });
  }

  close() {
    for (let key in this.clients) {
      this.clients[key].socket.end();
    }
    this.server.close();
  }

  error(e) {
    console.error('Server error:', e.message);
    process.exit(1);
  }
}

module.exports = Server;

