const http = require('http');
const fs = require('fs');
const path = require('path');
const send = require('send');
const marked = require('marked');

class Server {
  constructor(config) {
    this.config = config;
    this._http = null;
    this.sockets = [];
    this.path = config.path;
  }

  updateServer() {
    this._http = http.createServer((req, res) => {
      const reqPath = req.url === '/' ? '' : req.url;
      const fullPath = path.join(this.path, path.normalize(reqPath));

      if (!fullPath.startsWith(this.path)) {
        res.statusCode = 403;
        res.end('Forbidden');
        return;
      }

      const handleDirectory = (dirPath) => {
        fs.readdir(dirPath, (err, files) => {
          if (err) {
            res.statusCode = 500;
            res.end('Internal Server Error');
            return;
          }

          const indexPath = path.join(dirPath, 'index.html');
          fs.access(indexPath, fs.constants.F_OK, (err) => {
            if (!err) {
              send(req, indexPath).pipe(res);
              return;
            }

            let html = '<html><head><title>Index of ' + reqPath + '</title></head><body><h1>Index of ' + reqPath + '</h1><ul>';
            files.forEach(file => {
              const filePath = path.join(reqPath, file);
              html += `<li><a href="${filePath}">${file}</a></li>`;
            });
            html += '</ul></body></html>';

            res.setHeader('Content-Type', 'text/html');
            res.end(html);
          });
        });
      };

      fs.stat(fullPath, (err, stats) => {
        if (err) {
          if (err.code === 'ENOENT') {
            res.statusCode = 404;
            res.end('Not Found');
          } else {
            res.statusCode = 500;
            res.end('Internal Server Error');
          }
          return;
        }

        if (stats.isDirectory()) {
          if (!req.url.endsWith('/') && req.url !== '') {
            res.writeHead(301, { Location: req.url + '/' });
            res.end();
            return;
          }
          handleDirectory(fullPath);
        } else {
          send(req, fullPath).pipe(res);
        }
      });
    });

    this._http.on('connection', (socket) => {
      this.sockets.push(socket);
      socket.on('close', () => {
        const index = this.sockets.indexOf(socket);
        if (index > -1) {
          this.sockets.splice(index, 1);
        }
      });
    });
  }

  start(callback) {
    this.updateServer();
    this._http.listen(this.config.port, () => {
      console.log(`Server running at http://localhost:${this.config.port}/`);
      if (callback) callback();
    });
  }

  stop(callback) {
    this._http.close(() => {
      this.sockets.forEach(socket => {
        socket.destroy();
      });
      if (callback) callback();
    });
  }
}

module.exports = Server;

