                    loadedFiles.push(filePath);
                }
            });
        } catch (err) {
            console.error(`Error reading directory ${dirPath}:`, err);
        }
    }

    // Load static files at startup
    Object.values(ROOTS).forEach(loadDirectory);

    // Output loaded files to console
    console.log('Loaded files:');
    loadedFiles.forEach(file => console.log(` - ${file}`));

    const server = http.createServer((req, res) => {
        let filePath;

        // Handle virtual paths
        if (req.url.startsWith('/core/')) {
            filePath = path.join(ROOTS.src, req.url.substring(5));
        } else if (req.url.startsWith('/plugin/')) {
            filePath = path.join(ROOTS.dist, 'plugins', req.url.substring(8));
        } else if (req.url === '/combined.js') {
            // Dynamic JavaScript concatenation
            handleCombinedJS(res);
            return;
        } else {
            filePath = path.join(ROOTS.dist, req.url === '/' ? 'index.html' : req.url);
        }

        // Security: Prevent path traversal
        if (filePath.includes('..') || !filePath.startsWith(Object.values(ROOTS)[0]) && !filePath.startsWith(Object.values(ROOTS)[1])) {
            res.writeHead(403, { 'Content-Type': 'text/plain' });
            res.end('Forbidden');
            return;
        }

        fs.stat(filePath, (err, stats) => {
            if (err || !stats.isFile()) {
                // Fallback behavior for undefined routes
                serveFallback(res);
                return;
            }

            const ext = path.extname(filePath).toLowerCase();
            const mimeType = MIME_TYPES[ext] || 'application/octet-stream';

            if (mimeType.startsWith('text/') || mimeType === 'application/javascript') {
                // Serve text files
                fs.readFile(filePath, 'utf8', (err, data) => {
                    if (err) {
                        res.writeHead(500, { 'Content-Type': 'text/plain' });
                        res.end('Internal Server Error');
                        return;
                    }

                    res.writeHead(200, { 'Content-Type': mimeType });
                    res.end(data, 'utf8');
                });
            } else {
                // Pipe binary files directly to response
                res.writeHead(200, { 'Content-Type': mimeType });
                const readStream = fs.createReadStream(filePath);
                readStream.pipe(res);
            }
        });
    });

    function handleCombinedJS(res) {
        // Find all JS files in src and dist directories
        const jsFiles = loadedFiles.filter(file => file.endsWith('.js'));

        // Set headers for combined JS
        res.writeHead(200, { 'Content-Type': 'application/javascript' });

        // Stream each file
        let fileCount = jsFiles.length;
        if (fileCount === 0) {
            res.end('// No JavaScript files found');
            return;
        }

        jsFiles.forEach((filePath, index) => {
            const readStream = fs.createReadStream(filePath, 'utf8');
            readStream.on('end', () => {
                fileCount--;
                if (fileCount === 0) {
                    res.end('\n// End of combined JavaScript');
                }
            });
            readStream.pipe(res, { end: false });
        });
    }

    function serveFallback(res) {
        res.writeHead(404, { 'Content-Type': 'text/html' });
        fs.createReadStream(path.join(ROOTS.dist, '404.html')).pipe(res);
    }

    server.listen(PORT, '127.0.0.1', () => {
        console.log(`Server running at http://127.0.0.1:${PORT}/`);
    });
}

