    this.port = port;
    this.host = host;
    this.family = 'ipv4';
    this.tlsEnabled = !!options.tls;
    this.server = this.tlsEnabled ? tls.createServer(options.tls) : net.createServer();
    this.state = 'stopped';
    this.connections = 0;
    this.sockets = [];
  }

  start() {
    return new Promise((resolve, reject) => {
      if (this.state === 'running') {
        resolve();
        return;
      }

      this.server.on('listening', () => {
        this.state = 'running';
        resolve();
      });

      this.server.on('error', (err) => {
        reject(err);
      });

      this.server.on('connection', (socket) => {
        this.connections += 1;
        this.sockets.push(socket);

        socket.on('close', () => {
          this.connections -= 1;
          const index = this.sockets.indexOf(socket);
          if (index !== -1) {
            this.sockets.splice(index, 1);
          }
        });
      });

      this.server.listen({ host: this.host, port: this.port, family: this.family });
    });
  }

  destroy() {
    return new Promise((resolve, reject) => {
      if (this.state === 'destroyed') {
        resolve();
        return;
      }

      this.state = 'destroyed';

      const closePromises = this.sockets.map(socket => {
        return new Promise((res) => {
          if (socket.destroyed) {
            res();
          } else {
            socket.destroy();
            socket.on('close', res);
          }
        });
      });

      Promise.all(closePromises)
        .then(() => {
          if (this.server.listening) {
            this.server.close((err) => {
              if (err) {
                reject(err);
              } else {
                resolve();
              }
            });
          } else {
