class TcpReader extends EventEmitter {
  constructor(opts, transporter) {
    super();
    this.server = null;
    this.opts = opts || {};
    this.opts.port = this.opts.port || 0;
    this.opts.maxPacketSize = this.opts.maxPacketSize || 64 * 1024;
    this.transporter = transporter;
    this.Promise = transporter ? transporter.Promise : Promise;
    this.logger = (transporter && transporter.logger) ? transporter.logger : console;
    this.sockets = [];
  }

  listen() {
    const self = this;
    return new this.Promise((resolve, reject) => {
      self.server = net.createServer();
      self.server.on('error', (err) => {
        reject(err);
      });
      self.server.on('listening', () => {
        resolve(self.server.address().port);
      });
      self.server.on('connection', (socket) => {
        self.onTcpClientConnected(socket);
      });
      self.server.listen(self.opts.port);
    });
  }

  onTcpClientConnected(socket) {
    const self = this;
    this.sockets.push(socket);
    socket.__dataBuffer = Buffer.alloc(0);

    socket.on('data', (data) => {
      try {
        socket.__dataBuffer = Buffer.concat([socket.__dataBuffer, data]);

        while (socket.__dataBuffer.length > 0) {
          if (socket.__dataBuffer.length < 4) {
            break;
          }
          const length = socket.__dataBuffer.readUInt32BE(0);
          if (length > this.opts.maxPacketSize) {
            this.logger.error(`Received packet exceeds maxPacketSize: ${length}`);
            this.closeSocket(socket);
            return;
          }
          if (socket.__dataBuffer.length < 4 + length) {
            break;
          }
          const packet = socket.__dataBuffer.slice(4, 4 + length);
          socket.__dataBuffer = socket.__dataBuffer.slice(4 + length);
          this.emit('packet', packet, socket);
        }
      } catch (e) {
        this.logger.error(`Error processing data: ${e.stack}`);
        this.closeSocket(socket);
      }
    });

    socket.on('end', () => {
      self.closeSocket(socket);
    });

    socket.on('error', (err) => {
      self.logger.error(`Socket error: ${err.message}`);
      self.closeSocket(socket);
    });
  }

  closeSocket(socket) {
    const index = this.sockets.indexOf(socket);
    if (index !== -1) {
      this.sockets.splice(index, 1);
    }
    try {
      if (!socket.destroyed) {
        socket.destroy();
      }
    } catch (e) {
      // Ignore destroy errors
    }
    this.emit('disconnection', socket);
  }

