  _setupWeakRefs() {
    const weak = require('weak');
    const sessions = this.server.sessions;
    Object.keys(sessions).forEach(id => {
      const session = sessions[id];
      weak(session, () => {
        delete sessions[id];
        if (this.opts.proto && typeof this.opts.proto.onSessionEnd === 'function') {
          this.opts.proto.onSessionEnd(id);
        }
      });
    });
  }

  _handleConnection(stream) {
    const id = this._randomId();
    const session = new this.cons(stream, id, this.opts);
    this.server.sessions[id] = session;

    stream.on('data', data => {
      if (this.opts.proto && typeof this.opts.proto.onData === 'function') {
        this.opts.proto.onData(id, data);
