        };
        this.type = 'server';
    }

    bind(port, host, fn) {
        const self = this;
        this.settings.port = typeof port === 'number' ? port : 3000;
        if (typeof host === 'string') {
            this.settings.host = host;
        }
        if (typeof host === 'function') {
            fn = host;
        }
        this.server = net.createServer();
        this.server.on('connection', (sock) => {
            this.onconnect(sock);
        });
        this.server.on('error', (err) => {
            this.emit('error', err);
        });
        this.server.on('close', () => {
            this.emit('close');
        });
        let listenOptions = {};
        if (this.settings.path) {
            listenOptions.path = this.settings.path;
        } else {
            listenOptions.port = this.settings.port;
            listenOptions.host = this.settings.host;
        }
        this.server.listen(listenOptions, function () {
            if (fn) fn.call(self);
            self.emit('listening');
        });
        return this;
    }

    onconnect(sock) {
        this.addSocket(sock);
        sock.write('Connected to server\r\n');
        sock.on('data', (data) => {
            this.emit('data', sock, data);
        });
        sock.on('end', () => {
            this.removeSocket(sock);
            this.emit('disconnect', sock);
        });
        this.handleErrors(sock);
        this.emit('connect', sock);
    }

    addSocket(sock) {
        this.socks.push(sock);
    }

    removeSocket(sock) {
        const index = this.socks.indexOf(sock);
        if (index !== -1) {
            this.socks.splice(index, 1);
        }
    }

    handleErrors(sock) {
        sock.on('error', (err) => {
            this.emit('socketError', sock, err);
        });
    }

    closeServer(fn) {
        const self = this;
        if (this.server) {
            this.socks.forEach((sock) => {
                sock.destroy();
            });
            this.server.close(() => {
                if (fn) fn.call(self);
                self.emit('serverClosed');
