    return new Promise((resolve, reject) => {
      try {
        this.server = net.createServer(this.onTcpClientConnected.bind(this));
        this.server.once('error', (err) => {
          this.logger.error(`TCP server error: ${err.message}`);
          reject(err);
        });
        this.server.once('listening', () => {
          this.connected = true;
          resolve();
        });
        this.server.listen(this.opts.port);
      } catch (err) {
        this.logger.error(`Failed to create TCP server: ${err.message}`);
        reject(err);
      }
    });
  }

