const net = require('net');
const EventEmitter = require('events');

class Node extends EventEmitter {
  constructor(id) {
    super();
    this.id_ = id;
    this.peers_ = {};
    this.PORT = 3000;
    this.server_ = net.createServer((socket) => {
      socket.on('data', (data) => this.on_data(data, socket));
      socket.on('end', () => this.remove_peer(socket));
    });
    this.server_.on('error', (err) => this.emit('error', err));
    this.server_.listen(this.PORT + id, '127.0.0.1', () => {
      this.emit('ready');
    });
  }

  start() {
    return new Promise((resolve, reject) => {
      const connectToRandomPeers = () => {
        let attempts = 0;
        const maxAttempts = 5;
        while (attempts < maxAttempts) {
          const randomId = Math.floor(Math.random() * 100);
          if (randomId !== this.id_) {
            const client = new net.Socket();
            client.connect(this.PORT + randomId, '127.0.0.1', () => {
              client.write(JSON.stringify({ type: 'handshake', id: this.id_ }));
              client.on('data', (data) => this.on_data(data, client));
              client.on('end', () => this.remove_peer(client));
              this.add_peer(client, randomId);
              attempts++;
            });
            client.on('error', () => {
              attempts++;
            });
          } else {
            attempts++;
          }
        }
        resolve();
      };
      if (this.server_._handle) {
        this.once('ready', connectToRandomPeers);
      } else {
        connectToRandomPeers();
      }
    });
  }

  on_data(data, socket) {
    try {
      const message = JSON.parse(data.toString());
      if (message.type === 'handshake') {
        this.add_peer(socket, message.id);
        this.send(socket, { type: 'handshake_ack', id: this.id_ });
      } else {
        this.emit('message', message, socket.remoteAddress);
      }
    } catch (e) {
      this.emit('error', e);
    }
  }

  send(socket, data) {
    if (typeof socket === 'number') {
      const peerSocket = Object.entries(this.peers_).find(([id]) => Number(id) === socket)?.[1];
      if (peerSocket && !peerSocket.destroyed) {
        peerSocket.write(JSON.stringify(data));
      }
    } else if (!socket.destroyed) {
      socket.write(JSON.stringify(data));
    }
  }

  broadcast(data) {
    Object.values(this.peers_).forEach((socket) => {
      if (!socket.destroyed) {
        socket.write(JSON.stringify(data));
      }
    });
  }

  add_peer(socket, remote_id) {
    if (!this.peers_[remote_id] || this.peers_[remote_id].destroyed) {
      this.peers_[remote_id] = socket;
      this.emit('peerConnected', remote_id);
    }
  }

  remove_peer(socket) {
    for (const [id, s] of Object.entries(this.peers_)) {
      if (s === socket) {
        delete this.peers_[id];
        this.emit('peerDisconnected', Number(id));
        break;
      }
    }
  }

  list_peers() {
    return Object.keys(this.peers_).map(Number);
  }
}

