  const versionsEndpoint = '/versions';
  const schemaEndpoint = '/schema';
  const cacheDir = path.join(__dirname, 'cache');

  try {
    if (!fs.existsSync(cacheDir)) {
      fs.mkdirSync(cacheDir);
    }

    const versionsResponse = await axios.get(`${apiBaseUrl}${versionsEndpoint}`);
    const versions = versionsResponse.data;

    if (!Array.isArray(versions)) {
      throw new Error('Invalid versions data received');
    }

    for (const version of versions) {
      if (version.deprecated) {
        continue;
      }

      try {
        const schemaResponse = await axios.get(`${apiBaseUrl}${schemaEndpoint}?version=${version.name}`);
        const schemaData = schemaResponse.data;

        const filePath = path.join(cacheDir, `schema-${version.name}.json`);
        fs.writeFileSync(filePath, JSON.stringify(schemaData, null, 2));
        console.log(`Cached schema for version ${version.name}`);
      } catch (schemaError) {
        console.error(`Failed to fetch schema for version ${version.name}:`, schemaError.message);
        process.exit(1);
      }
    }

    const versionsFilePath = path.join(cacheDir, 'versions.json');
    fs.writeFileSync(versionsFilePath, JSON.stringify(versions, null, 2));
    console.log('Cached versions data successfully');
  } catch (error) {
    console.error('Failed to fetch versions:', error.message);
    process.exit(1);
  }
}

