const net = require('net');

class FlashPolicyServer {
  constructor(options, origins) {
    if (typeof options === 'number') {
      this.port = options;
      this.log = console.log;
      this.origins = origins || [];
    } else {
      this.port = options.port !== undefined ? options.port : 843;
      this.log = options.log || console.log;
      this.origins = options.origins || origins || [];
      this.server = options.server;
    }
    this.socket = null;
    this.buffer = null;
    this.compile();
  }

  listen(port, server, cb) {
    if (typeof port === 'function') {
      cb = port;
      port = 843;
    } else if (typeof server === 'function') {
      cb = server;
      server = undefined;
    }

    if (port !== undefined) this.port = port;
    if (server !== undefined) this.server = server;

    if (!this.socket) {
      this.socket = net.createServer(this.responder.bind(this));
    }

    const doListen = () => {
      this.socket.listen(this.port, () => {
        this.log(`Policy server listening on port ${this.port}`);
        if (cb) cb();
      });

      this.socket.on('error', err => {
        this.log(`Policy server error: ${err.message}`);
      });
    };

    if (this.server) {
      this.server.on('listening', () => {
        const addr = this.server.address();
        this.add(`*:${addr.port}`);
        this.compile();
        doListen();
      });
    } else {
      doListen();
    }

    return this;
  }

  responder(socket) {
    socket.on('data', data => {
      if (data.toString().indexOf('<policy-file-request/>') === 0) {
        socket.write(this.buffer);
        this.log('Sent policy file to', socket.remoteAddress);
      }
    });
  }

  compile() {
    let xml = '<?xml version="1.0"?>\n';
    xml += '<!DOCTYPE cross-domain-policy SYSTEM "/xml/dtds/cross-domain-policy.dtd">\n';
    xml += '<cross-domain-policy>\n';

    for (const origin of this.origins) {
      const [domain, port] = origin.split(':');
      if (port) {
        xml += `  <allow-access-from domain="${domain}" to-ports="${port}" />\n`;
      } else {
        xml += `  <allow-access-from domain="${domain}" />\n`;
      }
    }

    xml += '</cross-domain-policy>\x00';
    this.buffer = Buffer.from(xml, 'utf8');
    return this;
  }

  add(...origins) {
    this.origins.push(...origins);
    this.compile();
    return this;
  }

  remove(origin) {
    this.origins = this.origins.filter(o => o !== origin);
    this.compile();
    return this;
  }

  close() {
    if (this.socket && this.socket.listening) {
      this.socket.close(() => {
        this.log('Policy server closed');
      });
    }
    this.socket = null;
    return this;
  }
}

