const setupUDPServer = (argv, io, debug) => {
  const dgram = require('dgram');
  const server = dgram.createSocket('udp4');
  const streams = {};

  const addMessageToStream = (streamId, message) => {
    if (!streams[streamId]) {
      streams[streamId] = [];
    }
    streams[streamId].push(message);
    if (streams[streamId].length > 100) {
      streams[streamId].shift();
    }
  };

