    try {
      data = JSON.parse(msg.toString());
    } catch (e) {
      debug(`Invalid JSON received from ${rinfo.address}:${rinfo.port}`);
      return;
    }

    if (!data.streamId || !data.content) {
      debug(`Missing streamId or content in message from ${rinfo.address}:${rinfo.port}`);
      return;
    }

    const timestamp = Date.now();
    const storedMessage = {
      timestamp,
      content: data.content
    };

    addMessageToStream(data.streamId, storedMessage);

    io.emit('newMessage', { streamId: data.streamId, message: storedMessage });
    io.emit('updateStreams', Object.keys(streams));
