    const udpUrlPattern = /^udp:\/\/([^:]+):(\d+)$/;
    return udpUrlPattern.test(target);
  }

  static parseTarget(targetUrl) {
    const match = targetUrl.match(/^udp:\/\/([^:]+):(\d+)$/);
    if (!match) throw new Error('Invalid UDP target URL format');
    return { host: match[1], port: parseInt(match[2], 10) };
  }

  initialize() {
    this.udpServer = dgram.createSocket('udp4');
    this.udpServer.on('message', this.onResponseCallback.bind(this));
    this.udpServer.on('error', (err) => {
      this.cleanup();
      if (typeof this.callback === 'function') {
        this.callback({ error: err.message });
      }
    });
  }

  poll() {
    if (!this.udpServer) this.initialize();
    const message = Buffer.from('ping');
    this.udpServer.send(message, this.target.port, this.target.host);
    this.timeoutId = setTimeout(() => this.timeoutReached(), this.timeout);
  }

