const EventEmitter = require('events');

class Chrome extends EventEmitter {
  constructor(options = {}, notifier) {
    super();
    this.host = options.host || 'localhost';
    this.port = options.port || 9222;
    this.secure = options.secure || false;
    this.webSocketUrl = options.webSocketUrl || null;
    this._notifier = notifier || new EventEmitter();
    this._callbacks = {};
    this._nextCommandId = 1;
    this._ws = null;
  }

  _start() {
    return new Promise((resolve, reject) => {
      this._connectToWebSocket()
        .then(resolve)
        .catch(reject);
    });
  }

  _connectToWebSocket() {
    return new Promise((resolve, reject) => {
      const protocol = this.secure ? 'wss' : 'ws';
      const url = this.webSocketUrl || `${protocol}://${this.host}:${this.port}/devtools/browser`;
      this._ws = new WebSocket(url);

      this._ws.on('open', () => {
        resolve();
      });

      this._ws.on('message', (message) => {
        this._handleMessage(message);
      });

      this._ws.on('error', (error) => {
        this.emit('error', error);
        reject(error);
      });

      this._ws.on('close', () => {
        this._handleConnectionClose();
        reject(new Error('WebSocket connection closed'));
      });
    });
  }

  _handleConnectionClose() {
    this._ws = null;
    for (const id in this._callbacks) {
      if (this._callbacks[id]) {
        this._callbacks[id].reject(new Error('Connection closed'));
      }
    }
    this._callbacks = {};
  }

  _handleMessage(message) {
    try {
      const data = JSON.parse(message.toString());
      if (data.id && this._callbacks[data.id]) {
        if (data.error) {
          this._callbacks[data.id].reject(data.error);
        } else {
          this._callbacks[data.id].resolve(data.result);
        }
        delete this._callbacks[data.id];
      } else {
        this._notifier.emit(data.method, data.params);
        this.emit(data.method, data.params);
      }
    } catch (e) {
      this.emit('error', e);
    }
  }

  send(method, params = {}, sessionId = null, callback = null) {
    const command = { method, params };
    if (sessionId) {
      command.sessionId = sessionId;
    }

    const id = this._nextCommandId++;
    command.id = id;

    const promise = new Promise((resolve, reject) => {
      this._callbacks[id] = { resolve, reject };
    });

    try {
      this._ws.send(JSON.stringify(command));
    } catch (e) {
      this._callbacks[id].reject(e);
      delete this._callbacks[id];
      throw e;
    }

    if (callback) {
      promise.then(result => callback(null, result)).catch(callback);
      return;
    }

    return promise;
  }

  _enqueueCommand(method, params, sessionId, callback) {
    this.send(method, params, sessionId, callback);
  }

  close(callback) {
    const promise = new Promise((resolve, reject) => {
      if (!this._ws) {
        return resolve();
      }

      const cleanup = () => {
        this._ws.off('close', onClose);
        this._ws.off('error', onError);
        resolve();
      };

      const onClose = () => cleanup();
      const onError = (err) => {
        this.emit('error', err);
        cleanup();
      };

      this._ws.on('close', onClose);
      this._ws.on('error', onError);
      this._ws.close();
    });

    if (callback) {
      promise.then(() => callback()).catch(err => callback(err));
      return;
    }

    return promise;
  }
}

