class Chrome extends EventEmitter {
  constructor(options = {}, notifier) {
    super();
    this.host = options.host || 'localhost';
    this.port = options.port || 9222;
    this.secure = options.secure || false;
    this._notifier = notifier;
    this._callbacks = {};
    this._nextCommandId = 1;
    this.webSocketUrl = `${this.secure ? 'wss' : 'ws'}://${this.host}:${this.port}`;
    this._ws = null;
  }

  async _connectToWebSocket() {
    return new Promise((resolve, reject) => {
      this._ws = new WebSocket(this.webSocketUrl);
      this._ws.on('open', () => {
        if (this._notifier && this._notifier.connected) {
          this._notifier.connected();
        }
        resolve();
      });
      this._ws.on('close', () => {
        this.emit('disconnect');
      });
      this._ws.on('error', (err) => {
        reject(err);
      });
      this._ws.on('message', (data) => {
        try {
          const message = JSON.parse(data);
          this._handleMessage(message);
        } catch (e) {
          console.error('Error parsing message:', e);
        }
      });
    });
  }

  _handleMessage(message) {
    if (message.id && this._callbacks[message.id]) {
      const callback = this._callbacks[message.id];
      delete this._callbacks[message.id];
      if (message.error) {
        callback(message.error, null);
      } else {
        callback(null, message.result);
      }
    } else if (message.method) {
      this.emit(message.method, message.params);
      if (this._notifier && this._notifier.eventReceived) {
        this._notifier.eventReceived(message.method, message.params);
      }
    }
  }

  _enqueueCommand(method, params, sessionId, callback) {
    const id = this._nextCommandId++;
    const command = { id, method };
    if (params) command.params = params;
    if (sessionId) command.sessionId = sessionId;
    this._callbacks[id] = callback;
    this._ws.send(JSON.stringify(command));
  }

  send(method, ...args) {
    let params, sessionId, callback;
    args.forEach(arg => {
      if (arg === undefined) return;
      if (typeof arg === 'function') {
        callback = arg;
      } else if (typeof arg === 'string') {
        sessionId = arg;
      } else if (typeof arg === 'object') {
        params = arg;
      }
    });

    if (!callback) {
      return new Promise((resolve, reject) => {
        this._enqueueCommand(method, params, sessionId, (err, result) => {
          if (err) return reject(err);
          resolve(result);
        });
      });
    }

    this._enqueueCommand(method, params, sessionId, callback);
  }
}

module.exports = Chrome;

