      this._readyState = 0; // CONNECTING

      this._ws.on('open', () => {
        this._readyState = 1; // OPEN
        this._onConnectionEstablished();
      });

      this._ws.on('message', (data, isBinary) => {
        this._onMessageReceived(data, isBinary);
      });

      this._ws.on('close', (code, reason) => {
        this._readyState = 3; // CLOSED
        this._onConnectionClosed(code, reason);
      });

      this._ws.on('error', (err) => {
        if (this._requiredToFail) {
          this._onConnectionClosed(1006, 'Connection failed');
        }
      });
    } catch (error) {
      this._requiredToFail = true;
      process.nextTick(() => {
        this._onConnectionClosed(1006, 'Connection failed');
      });
    }
  }

  _onConnectionEstablished() {
    while (this._sendQueue.length > 0) {
      const message = this._sendQueue.shift();
      this._actuallySend(message);
    }
  }

  _onMessageReceived(data, isBinary) {
    const event = {
      data: isBinary ? data : data.toString(),
      type: isBinary ? 'binary' : 'text'
    };
    if (this.onmessage) {
      this.onmessage(event);
    }
  }

  _onConnectionClosed(code, reason) {
    if (this.onclose) {
      this.onclose({ code, reason });
    }
  }

  close(code, reason) {
    if (this._readyState === 0 || this._readyState === 1) {
      this._readyState = 2; // CLOSING
      this._ws.close(code, reason);
    }
  }

  send(data) {
    if (this._readyState === 1) { // OPEN
      this._sendQueue.push(data);
      this._scheduleSend();
    } else {
      this._sendQueue.push(data);
    }
