        this.PRIVATE_API_URL = 'https://api.cryptsy.com/api';
        this.USER_AGENT = 'nomp/node-open-mining-portal';
        this.STRICT_SSL = true;
    }
    Cryptsy.prototype._getPrivateHeaders = function (parameters) {
        var timestamp = Date.now().toString();
        var nonce = timestamp;
        var dataToSign = parameters.command + nonce;
        var signature = require('crypto').createHmac('sha512', this.secret).update(dataToSign).digest('hex');
        return {
            'User-Agent': this.USER_AGENT,
            'Authorization': 'Bearer ' + this.key,
            'X-Signature': signature,
            'X-Nonce': nonce
        };
    };
    Cryptsy.prototype._request = function (options, callback) {
        options.headers = options.headers || {};
        options.json = true;
        options.strictSSL = this.STRICT_SSL;
        request(options, function (error, response, body) {
            if (error) {
                return callback(error);
