class Binary {
  constructor(url, data = {}) {
    this.url = url;
    this.name = data.name || -1;
    this.installDirectory = data.installDirectory || 'bin';
    this.binaryDirectory = -1;
    this.binaryPath = -1;
  }

  _getInstallDirectory() {
    const path = require('path');
    return path.resolve(process.cwd(), this.installDirectory);
  }

  async install() {
    const fs = require('fs').promises;
    const path = require('path');
    const axios = require('axios');
    const AdmZip = require('adm-zip');
    const os = require('os');
    const tmpdir = os.tmpdir();

    try {
      const installDir = this._getInstallDirectory();
      await fs.mkdir(installDir, { recursive: true });

      const response = await axios.get(this.url, { responseType: 'arraybuffer' });
      const zipPath = path.join(tmpdir, `temp-binary-${Date.now()}.zip`);
      await fs.writeFile(zipPath, response.data);

      const zip = new AdmZip(zipPath);
      const extractTarget = installDir;
      zip.extractAllTo(extractTarget, true);

      await fs.unlink(zipPath);

      this.binaryDirectory = extractTarget;
      if (this.name !== -1) {
        this.binaryPath = path.join(extractTarget, this.name);
      } else {
        const files = await fs.readdir(extractTarget);
        if (files.length > 0) {
          this.binaryPath = path.join(extractTarget, files[0]);
        }
      }

      console.log(`Binary installed successfully at ${extractTarget}`);
    } catch (error) {
      console.error(`Installation failed: ${error.message}`);
      throw error;
    }
  }
}

