    this._sendQueue = [];

    this._connect();
  }

  _connect() {
    try {
      this._ws = new WebSocket(this.url);
      this._readyState = 0; // CONNECTING

      this._ws.onopen = () => {
        this._readyState = 1; // OPEN
        this._onConnectionEstablished();
        this._processSendQueue();
      };

      this._ws.onmessage = (event) => {
        let isBinary = typeof event.data === 'object' && event.data instanceof Buffer;
        this._onMessageReceived(event.data, isBinary);
      };

      this._ws.onclose = (event) => {
        this._readyState = 3; // CLOSED
        this._onConnectionClosed(event.code, event.reason);
      };

      this._ws.onerror = (error) => {
        this._requiredToFail = true;
        this.close(1006, 'Connection failed');
      };
    } catch (e) {
      this._requiredToFail = true;
      this.close(1006, 'Connection failed');
    }
  }

  _onConnectionEstablished() {
    // To be overridden by user if needed
  }

  _onMessageReceived(data, isBinary) {
    // To be overridden by user if needed
  }

  _onConnectionClosed(code, reason) {
    // To be overridden by user if needed
  }

  close(code = 1000, reason = 'Normal closure') {
    if (this._ws) {
      this._ws.close(code, reason);
      this._ws = null;
    }
    this._readyState = 3; // CLOSED
  }

  send(data) {
    if (typeof data !== 'string' && !(data instanceof Blob)) {
      throw new TypeError("Unsupported data type. Only string and Blob are supported.");
    }

    const sendImmediately = () => {
      if (this._readyState === 1) { // OPEN
        this._ws.send(data);
      } else {
        this._sendQueue.push(data);
        this.bufferedAmount += Buffer.byteLength(data);
      }
    };

    if (this._readyState === 0) { // CONNECTING
      this._connect(); // Ensure connection attempt
      return;
    }

    if (this._readyState === 1) { // OPEN
      sendImmediately();
    }
  }

  _processSendQueue() {
    while (this._sendQueue.length > 0 && this._readyState === 1) {
      const data = this._sendQueue.shift();
      this._ws.send(data);
      this.bufferedAmount -= Buffer.byteLength(data);
    }
  }

  get readyState() {
    return this._readyState;
  }
}

